package eclipsetest;

public class name {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("N  ");
		for(int i=0;i<4;i++) {
			if(i==0||i==3) {
			for(int j=0;j<6;j++) {
				System.out.print(" "+"*");
			}
			System.out.println("");
			}
			else {
				for(int j=0;j<6;j++) {
					if(j==0||j==5) {
						System.out.print(" "+"*");
					}
					else {
						System.out.print("  ");
					}
					}
				System.out.println("");
				}
			}
			
			
		}

	}


